const chatBox = document.getElementById('chat-box');
const chatForm = document.getElementById('chat-form');
const chatSelectForm = document.getElementById('chats_select_form');
const messageInput = document.getElementById('message-input');
const url = new URL(document.location.href)
const params = url.searchParams
let operator;
let chat;
let user;
let messages;
let chats;

const sound = new Audio("data:audio/wav;base64,");

let operator_id = params.get('operator_id');
if (operator_id === undefined) {
    console.log('Неизвестный оператор')
    document.getElementById('message-input').hidden = true;
} else {
    loadOperator(operator_id);
}

let chat_id = params.get('chat_id');
if (chat_id !== null) {
    loadChat(chat_id);
    document.getElementById('chats').hidden = true;
    document.getElementById('chat').hidden = false;
} else {
    loadChats();
}

chatSelectForm.addEventListener('submit', event => {
    event.preventDefault();
    let select = document.getElementById('chats_select')
    window.location.href = document.location.href + "&chat_id=" + select[select.selectedIndex].id;
});

let user_id = getCookie('user_id');
if (user_id === undefined) {
    loadUserByChat(chat_id)
}

loadMessagesByChat(chat_id);

const socket = new WebSocket('ws://localhost:2346/?chat_id=' + chat_id + '&operator_id=' + operator_id);

socket.addEventListener('open', event => {
    console.log('Connected to WebSocket server');
});

socket.addEventListener('message', event => {
    const message = JSON.parse(event.data);
    sound.play();
    console.log(message)
    if (message.type === 'system') {
        handleSystemMessage(message)
    } else {
        addMessageToChatBox(message);
    }
});

chatForm.addEventListener('submit', event => {
    event.preventDefault();
    const message = {
        text: messageInput.value,
        senders_id: operator.id,
        type: 'operator',
        chat_id: chat_id
    };
    socket.send(JSON.stringify(message));
    addMessageToChatBox(message);
    messageInput.value = '';
});

function addMessageToChatBox(message) {
    const messageElement = document.createElement('div');
    const userName       = user !== undefined
        ? user.name + '(Пользователь)'
        : '(Пользователь)';
    const contact = message.type !== 'operator' ? userName : 'Вы';
    messageElement.innerText = `${new Date().toLocaleTimeString()} ${contact}: ${message.text}`;
    chatBox.appendChild(messageElement);
    chatBox.scrollTop = chatBox.scrollHeight;
}

function loadUserByChat(id) {
    $.get("chat/user?id=" + id, function(data) {
        let response = JSON.parse(data);
        if (response.status !== 200) {
            console.log(response.message);
            return;
        }
        user = JSON.parse(response.body);
    });
}

function handleSystemMessage(message) {
    switch (message.text) {
        case 'question_closed':
            document.getElementById('active').hidden = true;
            document.getElementById('done').hidden = false;
            break;
        default:
            return;
    }
}

function loadChats() {
    $.get("chats/active", function(data) {
        let response = JSON.parse(data);
        if (response.status !== 200) {
            console.log(response.message);
            return;
        }
        chats = JSON.parse(response.body);
        showChats(chats)
    });
}

function showChats(chats) {
    let select = document.getElementById('chats_select');
    for (var i in chats) {
        let chat = new Option();
        chat.id = chats[i]['id'];
        chat.text = 'Чат' + chats[i]['id'];
        select.appendChild(chat);
    }
}