const chatBox = document.getElementById('chat-box');
const chatForm = document.getElementById('chat-form');
const messageInput = document.getElementById('message-input');
const finishButton  = document.getElementById('finish')
let user;
let chat;
let operator;
let socket;
let user_id;

const sound = new Audio("data:audio/wav;base64,");

let chat_id = getCookie('chat_id');
if (chat_id === undefined) {
    chat = createChat();
} else {
    loadChat(chat_id);
}

function init() {
    user_id = getCookie('user_id');
    if (user_id === undefined) {
        createUser(chat_id ?? getCookie('chat_id'));
    } else {
        loadUser(user_id);
    }

    let operator_id = getCookie('operator_id');
    if (operator_id === undefined) {
        loadOperatorByChat(chat_id);
    }

    if (chat_id !== undefined) {
        loadMessagesByChat(chat_id);
    }
    socket = new WebSocket('ws://localhost:2346/?chat_id=' + chat.id + '&user_id=' + user_id);

    socket.addEventListener('open', event => {
        console.log('Connected to WebSocket server');
    });

    socket.addEventListener('error', event => {
        console.log('Ошибка при подключении к WebSocket')
    })

    socket.addEventListener('message', event => {
        const message = JSON.parse(event.data);
        sound.play();
        if (message.type === 'system') {
            handleSystemMessage(message);
        } else {
            addMessageToChatBox(message);
        }
    });

    if (chat.status === 'closed') {
        finishButton.disabled = true;
    }
}

setTimeout(init, 1000);

chatForm.addEventListener('submit', event => {
    event.preventDefault();
    const message = {
        text: messageInput.value,
        senders_id: user_id ?? user.id,
        chat_id: chat.id,
        type: 'user'
    };
    socket.send(JSON.stringify(message));
    addMessageToChatBox(message);
    messageInput.value = '';
});

finishButton.addEventListener('click', event => {
    event.preventDefault();
    const message = {
        text: 'question_closed',
        senders_id: user.id,
        chat_id: chat.id,
        type: 'system'
    };
    socket.send(JSON.stringify(message));
    finishButton.disabled = true;
});

function addMessageToChatBox(message) {
    const messageElement = document.createElement('div');
    const operatorName   = operator !== undefined
        ? operator.name + '(Оператор)'
        : '(Оператор)';

    const contact  = message.type === 'operator' ? operatorName : 'Вы';
    const date     = new Date();
    const datetime = message.created_at === undefined
        ? date.toLocaleDateString().replace('20','') + " " + date.toLocaleTimeString()
        : message.created_at;

    messageElement.innerText = `${datetime} ${contact}: ${message.text}`;
    chatBox.appendChild(messageElement);
    chatBox.scrollTop = chatBox.scrollHeight;
}

function createUser(chatId) {
    $.ajax({
        type: 'POST',
        url: "user/create",
        data: {'chat_id': chatId},
        dataType: "json",
        success: function(data) {
            user = data
            document.cookie = "user_id="+ data.id;
        },
        error: function(jqXHR, textStatus, errorThrown) {
            console.log(jqXHR.responseText);
        },
    }).done(function (data) {
            return data
    });
}

function loadUser(id) {
    $.ajax({
        type: 'GET',
        url: "user?id=" + id,
        dataType: "json",
        success: function(data) {
            user = data;
        },
        error: function(jqXHR, textStatus, errorThrown) {
            createUser(chat_id)
        },
    });
}

function loadOperatorByChat(id) {
    $.ajax({
        type: 'GET',
        url: "chat/operator?id=" + id,
        dataType: "json",
        success: function(data) {
            operator = data;
            document.cookie = "operator_id="+ operator.id;
        },
        error: function(jqXHR, textStatus, errorThrown) {
            console.log(jqXHR.responseText);
        },
    });
}

function handleSystemMessage(message) {
    switch (message.text) {
        case 'operator_active':
            document.getElementById('offline').hidden = true;
            document.getElementById('online').hidden = false;
            loadOperatorByChat(chat.id)
            break;
        default:
            return;
    }
}